INSERT INTO `questions` VALUES ('f620422b-a7e5-41ca-b9cd-21c5073fc51b','101','4982c0da-e4ec-4bb3-8724-e84f6e4994f4','23d58d77-91d2-49a0-83ea-dde75a5d6df8','Which of the following comments best exemplifies a contextual comment explaining a non-obvious implementation choice?','[\"# Increment i by 1.\", \"# Use bubble sort because the list is nearly sorted and it outperforms quicksort in this scenario.\", \"# This function sorts a list.\", \"# TODO: improve the sorting algorithm.\"]','[1]','easy','[\"comments\", \"code style\"]','https://mitcommlab.mit.edu/broad/commkit/coding-mindset/',0,0,'2025-10-01 10:33:05','2025-10-01 10:33:05','mcq');
